import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { buscar } from "../api/api";

const ListaPost = ({ url }) => {
  const [posts, setPost] = useState([]);
  useEffect(() => {
    buscar(url, setPost);
  }, []);

  return (
    <section>
      {posts.map((post) => {
        <Link className={`catao-post cartao-post--${post.categoria}`}>
          <article key={post.id}>
            <h3 className="cartao-post__titulo">{post.title}</h3>
            <p className="catao-post__meta">{post.metadescription}</p>
          </article>
        </Link>;
      })}
    </section>
  );
};

export default ListaPost;
